sp_widget.script:
(function() {

	/*--Populate asset category dropdown--*/
	var catNames = [];
	var gr = new GlideRecord("x_134426_emp_asset_category");
	gr.query();
	while(gr.next()){
		catNames.push({
			"name": gr.getDisplayValue(),
			"sysid": gr.getValue("sys_id")
		});
	}
	data.catNames = catNames;


	/*--TODO: add suggestion to personnel assets table with Approved set to false--*/
	if(input){
		var empUser = new GlideRecord("x_134426_emp_user");

		/*--If a token is detected in URL, use token for authentication--*/
		if(input.tokenID){
			var tokenRecord = new GlideRecord("x_134426_emp_tokens");
			tokenRecord.addQuery('token', input.tokenID);
			tokenRecord.query();
			tokenRecord.next();

			/*--If the token is expired send back an error--*/
			if(tokenRecord.used){
				data.expiredTokenError = true;
				return;
			}
			empUser.addQuery("sys_id", tokenRecord.getValue("emp_user"));	
		}

		/*--otherwise use GlideSystem for authentication--*/
		else{
			empUser.addQuery("system_user", gs.getUserID());
		}
		empUser.query();
		empUser.next();


		/*--TODO: input.assetChoice needs to be changed to input.catChoice--*/
		var newAsset = new GlideRecord("x_134426_emp_personnel_asset");

		/*--TODO: EMP User name into sys_created_by field on insert--*/
		newAsset.initialize();
		newAsset.asset_name = input.assetName;
		newAsset.category = input.catChoice.sysid;
		newAsset.sys_created_by = empUser.name;
		newAsset.approved = false;
		newAsset.insert();

	}

})();
