sp_widget.client_script:
function($scope, $window) {
  /* widget controller */
  var c = this;

	c.emp_user = {
  displayValue: "",
  value: "",
  name: 'emp_user_field'
  };

	$scope.test = function() {
		console.log(c.emp_user);
	}

	$scope.send = function() {
		$scope.data.emp_user = c.emp_user;
		c.server.update();
	}

	$scope.windowReturn = function() {
		$window.location.reload();
	}
}

sp_widget.css:
.fade {
  opacity: 1;
  transition:all .3s linear;
}
.fade.ng-hide {
  opacity: 0;
}
.fade.ng-hide-remove {
  transition: all .3s linear;
  display: block !important;
}

.submitButton {
	float:right;
}

