sp_widget.script:
(function() {

	if(input){
		var empUser = new GlideRecord("x_134426_emp_user");
		empUser.addQuery("system_user", gs.getUserID());
		empUser.query();
		empUser.next();

		var newAsset = new GlideRecord("x_134426_emp_asset_category");

		newAsset.initialize();
		newAsset.category_name = input.catName;
		newAsset.sys_created_by = empUser.name;
		newAsset.insert();

	}

})();
