//Create a new token record.
var tokenRecord = new GlideRecord('x_134426_emp_tokens');
tokenRecord.query();
while(tokenRecord.next()){
	tokenRecord.used = false;
	gr.update();
	gs.eventQueue('x_134426_emp.notifyPotentialHire',
				  current, tokenRecord.emp_user.contact_email,
				  tokenRecord.sys_id);
}

